from configs import dbdb = db.connect()cursor = db.cursor()sql = "SELECT id, username, address FROM users"try:	cursor.execute(sql)	results = cursor.fetchall()	response = []	for row in results:		#print("userid: " , row[0], ", username: ", row[1], ", address: " + row[2])		response.append({"userid" : row[0], 						"username" : row[1], 						"address" : row[2]})			print(response)except Exception:	print("Error: cannot fetch the data")	db.close()